private String cleanBuffer(StringBuffer buffer) {
    StringBuilder cleanedBuffer = new StringBuilder();
    for (int i = 0; i < buffer.length(); i++) {
        char ch = buffer.charAt(i);
        if (ch != '\r') {
            cleanedBuffer.append(ch);
        }
    }
    return cleanedBuffer.toString();
}

