public void initialize(InputStream in) {
  try {
    if (in != null) {
      try {
        m_serverProperties.load(new InputStreamReader(new BOMInputStream(in), StandardCharsets.UTF_8));
      } finally {
        in.close();
      }
    }

    initEnvType();
    initDataCenter();
  } catch (Throwable ex) {
    logger.error("Initialize DefaultServerProvider failed.", ex);
  }
}

