public static String toString(float x) {
  if (x == 0) {
    return "0E0";
  }
  BigDecimal bigDecimal =
      new BigDecimal(x, MathContext.DECIMAL32).stripTrailingZeros();
  final String s = bigDecimal.toString();
  return s.replaceAll("0*E", "E").replace("E+", "E");
}

