    StringBuffer sb = new StringBuffer();
    sb.append(secStorageMountPoint);
    if (!secStorageMountPoint.endsWith("/"))
        sb.append("/");

    sb.append(templateRelativeFolderPath);
    if (!secStorageMountPoint.endsWith("/"))
        sb.append("/");

    sb.append(templateName);
