      if(vm.getType() == VirtualMachine.Type.User){
          UserVmVO uservm = _userVmDao.findById(vm.getId());
          displayVm = uservm.isDisplayVm();
      }

      _userVmMgr.generateUsageEvent(vm, displayVm, eventType);
  }

