public void testAppendSeparator_char_int() {
    final StrBuilder sb = new StrBuilder();
    sb.appendSeparator(',', 0);  
    assertEquals("", sb.toString());
    sb.append("foo");
    assertEquals("foo", sb.toString());
    sb.appendSeparator(',', 1);
    assertEquals("foo,", sb.toString());

    sb.appendSeparator(',', -1);  
    assertEquals("foo,", sb.toString());
}

