        Assert.assertEquals(actual_bar_foo, READ_CREATE_WRITE);
        List<ACL> actual_bar = client.getACL().forPath("/bar");
        Assert.assertEquals(actual_bar, READ_CREATE);
    }
    finally
    {
        CloseableUtils.closeQuietly(client);
    }
}

