        authTypeCombo = new Combo(addrGroup, SWT.BORDER | SWT.DROP_DOWN);
        authTypeCombo.add(""); 
        authTypeCombo.add("snowflake"); 
        authTypeCombo.add("externalbrowser"); 
        gd = new GridData(GridData.HORIZONTAL_ALIGN_BEGINNING | GridData.FILL_HORIZONTAL);
