private void listEvent(Event event)
{
    switch (event.type) {
        case SWT.Dispose:
            if (getShell() != this.popup.getParent()) {
                int selectionIndex = this.getSelectionIndex();
                this.popup = null;
                this.dropDownControl = null;
                createPopup();
            }
            break;
        case SWT.FocusIn: {
            handleFocus(SWT.FocusIn);
            break;
        }
        case SWT.MouseUp: {
            if (event.button != 1) {
                return;
            }
            dropDown(false);
            break;
        }
        case SWT.Selection: {
            Table table = this.dropDownControl;
            int index = table.getSelectionIndex();
            if (index == -1) {
                return;
            }
            final TableItem tableItem = table.getItem(index);
            ITEM_TYPE item = (ITEM_TYPE) tableItem.getData();
            select(item);
            table.setSelection(index);
            Event e = new Event();
            e.time = event.time;
            e.stateMask = event.stateMask;
            e.doit = event.doit;
            notifyListeners(SWT.Selection, e);
            event.doit = e.doit;
            break;
        }
        case SWT.Traverse: {
            switch (event.detail) {
                case SWT.TRAVERSE_RETURN:
                case SWT.TRAVERSE_ESCAPE:
                case SWT.TRAVERSE_ARROW_PREVIOUS:
                case SWT.TRAVERSE_ARROW_NEXT:
                    event.doit = false;
                    break;
            }
            Event e = new Event();
            e.time = event.time;
            e.detail = event.detail;
            e.doit = event.doit;
            e.character = event.character;
            e.keyCode = event.keyCode;
            notifyListeners(SWT.Traverse, e);
            event.doit = e.doit;
            event.detail = e.detail;
            break;
        }
        case SWT.KeyUp: {
            Event e = new Event();
            e.time = event.time;
            e.character = event.character;
            e.keyCode = event.keyCode;
            e.stateMask = event.stateMask;
            notifyListeners(SWT.KeyUp, e);
            break;
        }
        case SWT.KeyDown: {
            if (event.character == SWT.ESC) {
                
                dropDown(false);
            }
            if ((event.stateMask & SWT.ALT) != 0
                && (event.keyCode == SWT.ARROW_UP || event.keyCode == SWT.ARROW_DOWN)) {
                dropDown(false);
            }
            if (event.character == SWT.CR) {
                
                dropDown(false);
                Event e = new Event();
                e.time = event.time;
                e.stateMask = event.stateMask;
                notifyListeners(SWT.DefaultSelection, e);
            }
            
            
            if (isDisposed()) {
                break;
            }
            Event e = new Event();
            e.time = event.time;
            e.character = event.character;
            e.keyCode = event.keyCode;
            e.stateMask = event.stateMask;
            notifyListeners(SWT.KeyDown, e);
            break;

        }
        case SWT.Resize: {
            
            break;
        }
    }
}

