public void mouseUp(MouseEvent e) {
    if (e.button != 1) {
        
        return;
    }
    final TableItem item = table.getItem(new Point(e.x, e.y));
    if (item != null) {
        columnIndex = UIUtils.getColumnAtPos(item, e.x, e.y);
        UIUtils.asyncExec(new Runnable() {
            @Override
            public void run() {
                showEditor(item);
            }
        });
    }
}

