    EasyMock.expect(taskStorage.getTask(task.getId())).andReturn(Optional.of(task)).anyTimes();
  }
  EasyMock.replay(taskStorage);
  EasyMock.replay(taskClient);

  supervisor.runInternal();
  verifyAll();

  return tasks;
