                                      .setDimensions(new DefaultDimensionSpec("dim2", "d0"))
                                      .setAggregatorSpecs(
                                          ImmutableList.of(
                                              new DoubleSumAggregatorFactory("a0", "m1")
                                          )
                                      )
                                      .setContext(ImmutableMap.of(PlannerContext.CTX_SQL_QUERY_ID, "dummy"))
