	if (triggerResult.isFire()) {
		fire(context.window, contents);
	}

	if (triggerResult.isPurge() || (windowAssigner.isEventTime() && isCleanupTime(context.window, timer.getTimestamp()))) {
		cleanup(context.window, windowState, mergingWindows);
	}
}

