public void close() throws IOException {
	if (cache != null) {
		cache.cleanUp();
		cache = null;
	}
	if (statement != null) {
		try {
			statement.close();
		} catch (SQLException e) {
			LOG.info("JDBC statement could not be closed: " + e.getMessage());
		} finally {
			statement = null;
		}
	}

	if (dbConn != null) {
		try {
			dbConn.close();
		} catch (SQLException se) {
			LOG.info("JDBC connection could not be closed: " + se.getMessage());
		} finally {
			dbConn = null;
		}
	}
}

