	}
	
	schema = TableSchema.builder()
		.field(ROWKEY, STRING())
		.field(FAMILY1, ROW(
			FIELD(COL1, TIME(6)),
			FIELD(COL2, INT())))
		.build();

	try {
		createTableSource(schema, options);
		fail("Should fail");
