public void doRefCountChangedAfterGetRefCountInfoWithTrackRefsTrueAndTrackFreesTrue() throws Exception {
  rchi = getTrueTrue();
  long address = (long) 0x1000;
  boolean decRefCount = false;
  int rc = 1;

