    region.put("KEY" + i, p);
  }

  for (int i = 20; i < 24; i++) {
    Portfolio p = new Portfolio(11);
    p.shortID = 2;
    p.positions.clear();
    p.positions.put("VMW", new Position("VMW", i));
    region.put("KEY" + i, p);
  }
  for (int i = 24; i < 28; i++) {
    Portfolio p = new Portfolio(10);
    p.shortID = (short) (i % 3);
    p.positions.clear();
    p.positions.put("VMW", new Position("VMW", i));
    region.put("KEY" + i, p);
  }
  for (int i = 100; i < 200; i++) {
    Portfolio p = new Portfolio(10);
    p.shortID = 0;
    p.positions.clear();
    p.positions.put("VMW", new Position("VMW", i));
    region.put("KEY" + i, p);
  }
  
  

  
  Index idIndex = qs.createIndex("idIndex", "P.ID", "/portfolios1 P");
