@Override public void onAddJob(JobSpec addedJob) {
  super.onAddJob(addedJob);

  try {
    _jobSpecQueue.put(new ImmutablePair<SpecExecutor.Verb, Spec>(SpecExecutor.Verb.ADD, addedJob));
