public void testAddZone() throws Exception {
  int i = 0;
  for (String stateModel : _testModels) {
    String db = "Test-DB-testAddZone" + i++;
    createResourceWithWagedRebalance(CLUSTER_NAME, db, stateModel, PARTITIONS, _replica,
        _replica);
