    _allDBs.add(db);
  }
  Thread.sleep(300);

  validate(_replica);

  
  Set<MockParticipantManager> newNodes = new HashSet<>();
  Map<String, Integer> newNodeReplicaCount = new HashMap<>();

  ConfigAccessor configAccessor = new ConfigAccessor(_gZkClient);

