{
  String parentPath = new File(path).getParent();
  String childName = new File(path).getName();
  addToParentChildSet(parentPath, childName);

  ZNode znode = _cache.get(path);
  if (znode == null)
  {
    _cache.put(path, new ZNode(path, data, stat));
  }
  else
  {
    znode.setData(data);
    znode.setStat(stat);
  }
}

