public ManageCitationsDialogView(OOBibBase ooBase) {
    this.ooBase = ooBase;


    ViewLoader.view(this)
              .load()
              .setAsDialogPane(this);

    setResultConverter(btn -> {
        if (btn == ButtonType.OK) {
            viewModel.storeSettings();
        }
        return null;
    });

    setTitle(Localization.lang("Manage citations"));
}

