private String getCharset(URLConnection con) {
    for( String t : con.getContentType().split(";") ) {
        t = t.trim().toLowerCase();
        if(t.startsWith("charset="))
            return t.substring(8);
    }
    
    
    
    
    return "UTF-8";
}

