public static String escape(String text) {
    StringBuffer buf = new StringBuffer(text.length()+64);
    for( int i=0; i<text.length(); i++ ) {
        char ch = text.charAt(i);
        if(ch=='\n')
            buf.append("<br>");
        else
        if(ch=='<')
            buf.append("&lt;");
        else
        if(ch=='&')
            buf.append("&amp;");
        else
        if(ch==' ')
            buf.append("&nbsp;");
        else
            buf.append(ch);
    }
    return buf.toString();
}

