public void panDomainAxes(double percent, PlotRenderingInfo info,
        Point2D source) {
    if (!isDomainPannable()) {
        return;
    }
    int domainAxisCount = getDomainAxisCount();
    for (int i = 0; i < domainAxisCount; i++) {
        ValueAxis axis = getDomainAxis(i);
        if (axis == null) {
            continue;
        }
        if (axis.isInverted()) {
            percent = -percent;
        }
        axis.pan(percent);
    }
}

