public void testSetSectionBadNames() throws Exception {
	ConfigStore s = initStore("Foo.cfg");
	ConfigMap cm = s.getMap("Foo.cfg");

	String[] test = {
		"/", "[", "]",
		"foo/bar", "foo[bar", "foo]bar",
		" ",
		null
	};

	for (String t : test) {
		assertThrown(()->cm.setSection(t, null)).contains("Invalid section name");
	}
}

