public void testRecursion() throws Exception {
	UrlEncodingSerializerBuilder s = UrlEncodingSerializer.create().maxDepth(Integer.MAX_VALUE);

	R1 r1 = new R1();
	R2 r2 = new R2();
	R3 r3 = new R3();
	r1.r2 = r2;
	r2.r3 = r3;
	r3.r1 = r1;

	
	assertThrown(()->s.build().serialize(r1)).contains("It's recommended you use the BeanTraverseContext.BEANTRAVERSE_detectRecursions setting to help locate the loop.");

	
	s.detectRecursions();
	assertThrown(()->s.build().serialize(r1)).contains("[0] root:org.apache.juneau.urlencoding.Common_UrlEncodingTest$R1", "->[1] r2:org.apache.juneau.urlencoding.Common_UrlEncodingTest$R2", "->[2] r3:org.apache.juneau.urlencoding.Common_UrlEncodingTest$R3", "->[3] r1:org.apache.juneau.urlencoding.Common_UrlEncodingTest$R1");

	s.ignoreRecursions();
	assertEquals("name=foo&r2=(name=bar,r3=(name=baz))", s.build().serialize(r1));
}

