public FluentListAssertion<String,R> messages() {
	List<String> l = null;
	Throwable t = orElse(null);
	if (t == null)
		l = emptyList();
	else if (t.getCause() == null)
		l = singletonList(t.getMessage());
	else {
		l = new ArrayList<>();
		while (t != null) {
			l.add(t.getMessage());
			t = t.getCause();
		}
	}
	return new FluentListAssertion<>(this, l, returns());
}

