public static byte[] fromHex(String hex) {
	ByteBuffer buff = ByteBuffer.allocate(hex.length()/2);
	for (int i = 0; i < hex.length(); i+=2)
		buff.put((byte)Integer.parseInt(hex.substring(i, i+2), 16));
	buff.rewind();
	return buff.array();
}

