public static int parseIntWithSuffix(String s) {
	assertFieldNotNull(s, "s");
	int m = 1;
	if (s.endsWith("G")) {
		m = 1024*1024*1024;
		s = s.substring(0, s.length()-1).trim();
	} else if (s.endsWith("M")) {
		m = 1024*1024;
		s = s.substring(0, s.length()-1).trim();
	} else if (s.endsWith("K")) {
		m = 1024;
		s = s.substring(0, s.length()-1).trim();
	}
	return Integer.parseInt(s) * m;
}

