public static void validateXml(Object o, XmlSerializer s) throws Exception {
	s = s.builder().ws().ns().addNamespaceUrisToRoot().build();
	String xml = s.serialize(o);

	try {
		checkXmlWhitespace(xml);
	} catch (Exception e) {
		System.err.println("---XML---");       
		System.err.println(xml);               
		throw e;
	}
}

