    ArrayList<Map<String, String>> configs = new ArrayList<>();
    for (Integer i = 0; i < maxTasks; i++) {
        Map<String, String> props = new HashMap<>(config);
        props.put(VerifiableSinkTask.ID_CONFIG, i.toString());
        configs.add(props);
    }
    return configs;
}

