public int compareTo( Item item )
{
    if ( item == null )
    {
        return ( value == 0 ) ? 0 : 1; 
    }

    switch ( item.getType() )
    {
        case INT_ITEM:
            int itemValue = ( (IntItem) item ).value;
            return ( value < itemValue ) ? -1 : ( ( value == itemValue ) ? 0 : 1 );
        case LONG_ITEM:
        case BIGINTEGER_ITEM:
            return -1;

        case STRING_ITEM:
            return 1; 

        case LIST_ITEM:
            return 1; 

        default:
            throw new IllegalStateException( "invalid item: " + item.getClass() );
    }
}

