  Assert.assertEquals(response.getStatus(), ControllerResponseStatus.HOLD);
  
  Assert.assertFalse((fsmMap.containsKey(segmentNameStr)));
  Assert.assertFalse(commitTimeMap.containsKey(tableName));
}
