void runCmd() throws Exception {
    admin.sink().getBuiltInSinks().stream().filter(x -> isNotBlank(x.getSinkClass()))
            .forEach(connector -> {
                System.out.println(connector.getName());
                System.out.println(WordUtils.wrap(connector.getDescription(), 80));
                System.out.println("----------------------------------------");
            });
}

