                KeyValue keyValue = new KeyValue();
                keyValue.setKey(data.getKey());
                keyValue.setValue(data.getValue());
                logEntity.getData().add(keyValue);
            });

            span.getLogs().add(logEntity);
        });

        spans.add(span);
    });

    return spans;
}

