public static String setupSolrHome() throws Exception {
  
  File tmpFile = File.createTempFile("test", "tmp", TEMP_DIR);
  tmpFile.delete();
  tmpFile.mkdir();

  
  new File(tmpFile + "/collection1", "data").mkdirs();
