Set<CollectionStateWatcher> getStateWatchers(String collection) {
  final Set<CollectionStateWatcher> watchers = new HashSet<>();
  collectionWatches.compute(collection, (k, v) -> {
    if (v != null) {
      watchers.addAll(v.stateWatchers);
    }
    return v;
  });
  return watchers;
}

