        PayloadDecoder decoder = PayloadUtils.getPayloadDecoder(fieldType);
        return new FloatPayloadValueSource(
            tinfo.field,
            tinfo.val,
            tinfo.indexedField,
            tinfo.indexedBytes.get(),
            decoder,
            payloadFunction,
            defaultValueSource);
      }
