public void testTrimClasses() throws Exception {
  SolrInputDocument d = null;
  d = processAdd("trim-classes", 
                 doc(f("id", "1111"),
                     f("foo_t", " string1 "),
                     f("foo_s", " string2 "),
                     f("bar_dt", " string3 "),
