public static int readVInt(FastInputStream in) throws IOException {
  byte b = in.readByte();
  int i = b & 0x7F;
  for (int shift = 7; (b & 0x80) != 0; shift += 7) {
    b = in.readByte();
    i |= (b & 0x7F) << shift;
  }
  return i;
}

