  try (ZkStateReader zkStateReader = ClusterStateMockUtil.buildClusterState("csr*sr2csr*", "baseUrl1_", "baseUrl2_")) {
    CloudReplicaSource cloudReplicaSource = new CloudReplicaSource.Builder()
        .collection("collection1")
