public int[] resize(int[] old, int defaultValue) {
  int[] values = new int[getNewSize()];
  if (defaultValue != 0) {
    Arrays.fill(values, 0, values.length, defaultValue);
  }
  for (int i = 0; i < old.length; i++) {
    int val = old[i];
    if (val != defaultValue) {
      int newSlot = getNewSlot(i);
      if (newSlot >= 0) {
        values[newSlot] = val;
      }
    }
  }
  return values;
}

