public void foldReduce() {
  JavaRDD<Integer> rdd = sc.parallelize(Arrays.asList(1, 1, 2, 3, 5, 8, 13));
  Function2<Integer, Integer, Integer> add = (a, b) -> a + b;

  int sum = rdd.fold(0, add);
  Assert.assertEquals(33, sum);

  sum = rdd.reduce(add);
  Assert.assertEquals(33, sum);
}

